import React from 'react';
import ReactDOM from 'react-dom';
// import './index.css';
// import App from './App';
// import * as serviceWorker from './serviceWorker';
const Head = (props) => {
  return (
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
  return (
     <div>
       <Part part = {props.part[0].name} exercises = {props.part[0].exercises}/>
       <Part part = {props.part[1].name} exercises = {props.part[1].exercises}/>
       <Part part = {props.part[2].name} exercises = {props.part[2].exercises}/>
     </div>
  )
}

const Part = (props) => {
  return (
    <div>
      <p>{props.part} {props.exercises}</p>
    </div>
  )
}

const Total = (props) => {
  return (
    <div>
      <p>
      Number of exercises  {props.num}
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  // const part1 = {
  //   name: 'Fundamentals of React',
  //   exercises: 10
  // }
  // const part2 = {
  //   name: 'Using props to pass data',
  //   exercises: 7
  // }
  // const part3 = {
  //   name: 'State of a component',
  //   exercises: 14
  // }
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Head course = {course}/>
      <Content part = {parts} />
      <Total num = {part1.exercises + part2.exercises + part3.exercises}/>
    </div>
  )
}


ReactDOM.render(

   <App />,

  document.getElementById('root')
);

