import React,{ useState } from 'react';
import ReactDOM from 'react-dom';
import './index.css';

const Button = ({clickEvent,text}) => {

  return (
    <div style = {{float:'left'}}>
      <button onClick = {clickEvent}>{text}</button>
    </div>
  )
}

const Statistics = (props) => {
  const {title,num} = props
  return (
    <div>
      {title} {num}
    </div>
  )
}
const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const all = good + neutral + bad

  return (
    <div>
      <h1>give feedback</h1>
      <Button clickEvent = {() => {setGood(good + 1)}} text = {'good'} />
      <Button clickEvent = {() => {setNeutral(neutral + 1)}} text = {'neutral'} />
      <Button clickEvent = {() => {setBad(bad + 1)}} text = {'bad'} /><br/>
      <h1>statistics</h1>
      <Statistics title = {'good'} num = {good}/>
      <Statistics title = {'neutral'} num = {neutral}/>
      <Statistics title = {'bad'} num = {bad}/>
      <Statistics title = {'all'} num = {all}/>
      <Statistics title = {'average'} num = { (good - bad)/all}/>
      <Statistics title = {'positive'} num = {((good/all)/100+' %')}/>
    </div>
  )
}

ReactDOM.render(

    <App />
  ,
  document.getElementById('root')
);

